script.script:
/**
 * @description - Search an object in array
 * @param    {Array}   sourceArray - Array to search
 * @param    {Object}  object      - Object to search
 * @returns  {Object}  return      - Object if found, otherwise null
 * @version  1.0
 */

for (var i=0; i < sourceArray.length; i++) {

      if (sourceArray[i] == object) return sourceArray[i];
}

return null;
