script.script:
/**
 * @description - Check if value exists in the array
 * @param    {String}  value - Value
 * @param    {[Array]}  array - array to verify if exists the value
 * @returns  {Boolean} returns a boolean
 * @version  1.0
 */

if(array && value)
{
    for(var i=0;i<array.length;i++)
    {
        if(array[i] == value)
            return true;
    }

}

return false;
