    list = loadDefaults();
    omMessaging.cache_resetAll();
}

for(var i=0; i<list.length; i++){

    var conf = list[i];

    if(interfaceInvoked == conf.interfaceInvoked && operationInvoked == conf.operationInvoked){
        return [conf.queueGP, conf.queueOperation];
    }
}

for(var i=0; i<list.length; i++){

    var conf = list[i];

    if(oper == conf.operation){
        return [conf.queueGP, conf.queueOperation];
    }
}

return null;


