script.script:
/* * @param
 *            docArr                  Array                  The Array of Documents to Delete
 *            date                  Date/Time            The Fire And Proceed Action Date
 *            action                  Action                  The Fire And Proceed Action
 *
 */

 var NAME_SPACE            = "omMessaging";
 var SCRIPT_NAME      = "deleteMessage";
 var doc                  = null;

 try{
       // Delete the Selected Items
       for(var i=0; i<docArr.length; i++){
             doc = docArr[i];
             if(doc.action == omMessaging.WAITING_ACTION()){
                   // Move the Message to Fire And Proceed Archive
                   omMessaging.moveMsg2Arc(doc, date, action, false);
             }
       }
 }catch(e){
       //omCommon.writeLog(3, "Messaging: Error Deleting the Message " + e, NAME_SPACE + "." + SCRIPT_NAME);
       publishEvent("OM_COMMON",FIRST_ONE,null);
 }
