script.script:
var arrStacks = new Array();
var  usingDefault = false;

// Has configuration by Interface / Operation ?
var confsInterface = omMessaging.aux_getInterfaceToStack_Inbound(interfaceToInvoke, operation);


if(confsInterface != null && confsInterface.length > 0){

    arrStacks = confsInterface;

}else{
    // Has configuration by System

    var confSystem = omMessaging.aux_getSystemToStack_Inbound(system);

    if(confSystem != null && confSystem.length > 0){

        arrStacks = confSystem;
    }
}

var now = new Date();
var time = now.getTime();

if(arrStacks.length > 0){

    var stackId =  RandomStack(arrStacks, time);

    if(omMessaging.aux_validStack(stackId, "IN")){
        return stackId;
    }else{
        arrStacks = null;
        omMessaging.log("Invalid Stack ID, use default. STACKID: " + stackId , "in_getStackId");

    }
}


if(arrStacks == null || arrStacks.length == 0){

    //Use Default;

    arrStacks = omMessaging.aux_getDefaultStacks("IN");

    if(arrStacks.length > 0){
        var stackId =  RandomStack(arrStacks, time);

        if(omMessaging.aux_validStack(stackId, "IN")){
            return stackId;
        }else{
            omMessaging.log("Invalid Stack ID, Invalid default configuration. Messaging will STOP!!!", "in_getStackId", "ERR");
            return -1;
        }
    }
}



omMessaging.log("Invalid Stack ID, missing configurations. Messaging will STOP!!!", "in_getStackId", "ERR");


return -1;


/*
* ###########################################################################
*                     AUXILIAR METHODS
* ##########################################################################3
* /
*/



function RandomStack(arr, time){

    var nStacks = arr.length;

    var tmp =    (time%=nStacks);

    if(tmp < 0){
        tmp = tmp * -1;
    }

    if(tmp < 0 || tmp > nStacks){
        omMessaging.log("Invalid Stack ID:" + tmp + ", use first", "in_getStackId - RandomStack");
        tmp = 0;
    }

    return arr[tmp].stackId; ;

}
